#!/usr/bin/env python3\n# -*- coding: utf-8 -*-\n\"\"\"\nTeste R√°pido do Sistema - Solidar Bairro\n\"\"\"\n\nimport json\nimport sys\nimport os\n\n# Adiciona o diret√≥rio atual ao path\nsys.path.append(os.path.dirname(os.path.abspath(__file__)))\n\nfrom solidar_bot_robust import SolidarBotRobust\n\ndef quick_test():\n    \"\"\"Teste r√°pido do sistema\"\"\"\n    bot = SolidarBotRobust()\n    \n    print(\"üöÄ TESTE R√ÅPIDO DO SISTEMA DE VALIDA√á√ÉO\")\n    print(\"=\" * 50)\n    \n    # Teste 1: Pedido v√°lido\n    print(\"\\n‚úÖ Teste 1: Pedido V√°lido\")\n    valid_request = {\n        'category': 'Alimentos',\n        'description': 'Preciso urgentemente de ajuda com comida para minha fam√≠lia. Somos 4 pessoas, tenho 2 filhos pequenos de 3 e 5 anos. Estou desempregado h√° 3 meses e nossa situa√ß√£o est√° muito dif√≠cil.',\n        'urgency': 'critico'\n    }\n    \n    result = bot.validate_request(valid_request)\n    print(f\"   Resultado: {'‚úÖ APROVADO' if result['isValid'] else '‚ùå REJEITADO'}\")\n    print(f\"   Confian√ßa: {result['confidence']}%\")\n    print(f\"   Risco: {result['riskScore']}%\")\n    \n    # Teste 2: Pedido inv√°lido (spam)\n    print(\"\\n‚ùå Teste 2: Pedido com Spam\")\n    spam_request = {\n        'category': 'Alimentos',\n        'description': 'dawdawdaw dawdawdaw preciso ajuda dawdawdaw',\n        'urgency': 'urgente'\n    }\n    \n    result = bot.validate_request(spam_request)\n    print(f\"   Resultado: {'‚úÖ APROVADO' if result['isValid'] else '‚ùå REJEITADO'}\")\n    print(f\"   Confian√ßa: {result['confidence']}%\")\n    print(f\"   Risco: {result['riskScore']}%\")\n    if result['suggestions']:\n        print(f\"   Sugest√µes: {len(result['suggestions'])}\")\n    \n    # Teste 3: Categoria incorreta\n    print(\"\\n‚ùå Teste 3: Categoria Incorreta\")\n    wrong_category = {\n        'category': 'Medicamentos',\n        'description': 'Preciso de comida para meus filhos, estamos com fome h√° dias. N√£o temos nada para comer em casa.',\n        'urgency': 'critico'\n    }\n    \n    result = bot.validate_request(wrong_category)\n    print(f\"   Resultado: {'‚úÖ APROVADO' if result['isValid'] else '‚ùå REJEITADO'}\")\n    print(f\"   Confian√ßa: {result['confidence']}%\")\n    print(f\"   Risco: {result['riskScore']}%\")\n    if 'suggestedCategory' in result.get('validations', {}).get('categoryMatch', {}):\n        suggested = result['validations']['categoryMatch']['suggestedCategory']\n        print(f\"   Categoria Sugerida: {suggested}\")\n    \n    print(\"\\n\" + \"=\" * 50)\n    print(\"üéØ Sistema funcionando! Pronto para uso.\")\n\nif __name__ == '__main__':\n    quick_test()